function switch_radio(a){$(a).parent().siblings().children("input[type='radio']").uCheck("uncheck")}function encrypt_tool(){var a=$("#text-input").val();var d=$("#text-output");var c=$("#type-method input[type='radio']:checked").attr("name");var b=$("#key-word-input").val();if(!a){alert("请输入文本:)");return false}if(c=="aes"){d.val(CryptoJS.AES.encrypt(a,b));return}if(c=="des"){d.val(CryptoJS.DES.encrypt(a,b))}if(c=="triple-des"){d.val(CryptoJS.TripleDES.encrypt(a,b))}if(c=="rabbit"){d.val(CryptoJS.Rabbit.encrypt(a,b))}if(c=="rabbitlegacy"){d.val(CryptoJS.RabbitLegacy.encrypt(a,b))}if(c=="rc4"){d.val(CryptoJS.RC4.encrypt(a,b))}if(c=="rc4drop"){d.val(CryptoJS.RC4Drop.encrypt(a,b))}}function decrypt_tool(){var a=$("#text-input");var d=$("#text-output").val();var c=$("#type-method input[type='radio']:checked").attr("name");var b=$("#key-word-input").val();if(!d){alert("请输入文本:)");return false}if(c=="aes"){a.val(CryptoJS.AES.decrypt(d,b).toString(CryptoJS.enc.Utf8))}if(c=="des"){a.val(CryptoJS.DES.decrypt(d,b).toString(CryptoJS.enc.Utf8))}if(c=="triple-des"){a.val(CryptoJS.TripleDES.decrypt(d,b).toString(CryptoJS.enc.Utf8))}if(c=="rabbit"){a.val(CryptoJS.Rabbit.decrypt(d,b).toString(CryptoJS.enc.Utf8))}if(c=="rabbitlegacy"){a.val(CryptoJS.RabbitLegacy.decrypt(d,b).toString(CryptoJS.enc.Utf8))}if(c=="rc4"){a.val(CryptoJS.RC4.decrypt(d,b).toString(CryptoJS.enc.Utf8))}if(c=="rc4drop"){a.val(CryptoJS.RC4Drop.decrypt(d,b).toString(CryptoJS.enc.Utf8))}};