function goencrypt(b){var d=$("#"+b+"-input").val();var a=$("#"+b+"-output");if(!d){alert("请输入文本:)");return false}if(b=="md5"){a.text(CryptoJS.MD5(d))}if(b=="sha1"){a.text(CryptoJS.SHA1(d))}if(b=="sha224"){a.text(CryptoJS.SHA224(d))}if(b=="sha256"){a.text(CryptoJS.SHA256(d))}if(b=="sha384"){a.text(CryptoJS.SHA384(d))}if(b=="sha512"){a.text(CryptoJS.SHA512(d))}if(b=="ripemd-160"){a.text(CryptoJS.RIPEMD160(d))}if(b=="sha3"){select_radio=$("#"+b+"-input").siblings("label").children("input[type='radio']:checked").attr("name");a.text(CryptoJS.SHA3(d,{outputLength:select_radio}))}if(b=="hmac"){var c=$("#"+b+"-h-input").val();select_radio=$("#"+b+"-input").siblings("label").children("input[type='radio']:checked").attr("name");if(!c){alert("请输入认证码:)");return false}if(select_radio=="md5"){a.text(CryptoJS.HmacMD5(d,c))}if(select_radio=="sha1"){a.text(CryptoJS.HmacSHA1(d,c))}if(select_radio=="sha256"){a.text(CryptoJS.HmacSHA256(d,c))}if(select_radio=="sha512"){a.text(CryptoJS.HmacSHA512(d,c))}if(select_radio=="sha3"){a.text(CryptoJS.HmacSHA3(d,c))}if(select_radio=="ripemd-160"){a.text(CryptoJS.HmacRIPEMD160(d,c))}}if(b=="pbkdf2"){salt=$("#pbkdf2-salt-input").val();iter=parseInt($("#pbkdf2-iter-input").val());keySize=parseInt($("#pbkdf2-size-select").val());if(!salt){alert("请输入盐:)");return false}if(isNaN(iter)){alert("迭代次数写错啦，应该是个自然数:)");return false}if(iter>=1000){alert("您输入的迭代次数过大，计算时间可能会很长，这取决于您电脑性能:)")}a.text(CryptoJS.PBKDF2(d,salt,{keySize:keySize/32,iterations:iter}))}}function switch_radio(a){$(a).parent().siblings().children("input[type='radio']").uCheck("uncheck")};